      out.write("\t\t\t\r\n");
      out.write("\t\t\t// 가맹점에서 생성/관리하는 고유 주문번호 (필수항목)\r\n");
      out.write("\t\t\t// 결제가 된 적이 있는 merchant_uid로는 재결제 불가\r\n");
