      out.write("\t\t\t\t\t\tmsg += '카드 승인번호 : ' + rsp.apply_num;\r\n");
      out.write("\t\t\t\t\r\n");
      out.write("\t\t\t\t\t\talert(msg);\r\n");
      out.write("\t\t\t\t\t\tlocation.href=\"mypage\";\r\n");
      out.write("\t\t\t\t\t\t\r\n");
      out.write("\t\t\t\t\t} else {\r\n");
      out.write("\t\t\t\t\t\talert(\"결제 실패\");\r\n");
      out.write("\t\t\t\t\t\t//[3] 아직 제대로 결제가 되지 않았습니다.\r\n");
      out.write("\t\t\t\t\t\t//[4] 결제된 금액이 요청한 금액과 달라 결제를 자동취소처리하였습니다.\r\n");
      out.write("\t\t\t\t\t}\r\n");
      out.write("\t\t\t\t});\r\n");
