      out.write("\t<script>\r\n");
      out.write("\t\r\n");
      out.write("\t\t$(function(){\r\n");
      out.write("\t\t\t\r\n");
      out.write("\t\t\t$(\"textarea\").on(\"input\", function(){\r\n");
