      out.write("\t\t\t\r\n");
      out.write("\t\t\t// 이미지 공간을 클릭할 때 파일 첨부 창이 뜨도록 설정하는 함수\r\n");
      out.write("   \t    \t// 파일 선택 버튼이 있는 영역을 보이지 않게 함.\r\n");
      out.write("\t\t\t$(\"#fileArea\").hide();\r\n");
      out.write("\t\t\t\r\n");
      out.write("\t\t\t// 이미지 영역 클릭 시 파일 첨부창 띄우기\r\n");
      out.write("   \t    \t$(\"#thumbnail\").click(function(){\r\n");
      out.write("   \t    \t\t$(\"#onstudy-thumbnail\").click();\r\n");
      out.write("   \t    \t});\r\n");
      out.write("\t\t\t\r\n");
      out.write("\t\t\t\r\n");
      out.write("\t\t\t\r\n");
      out.write("\t\t\t// 입력된 기간(주) 저장\r\n");
      out.write("\t\t\tvar weeks = $(\"#onstudy-weeks\").val();\r\n");
      out.write("\t\t\t\r\n");
      out.write("\t\t\t// 종료일 datepicker의 'yy-mm-dd'형식으로 설정\r\n");
      out.write("\t\t\t// 종료일 기본값 지정(시작일의 +2주)\r\n");
      out.write("\t\t\t$(\"#onstudy-end\").datepicker({dateFormat: 'yy-mm-dd'});\r\n");
      out.write("\t\t\t$(\"#onstudy-end\").datepicker(\"setDate\", \"+\"+(weeks*7)+\"D\");\r\n");
      out.write("\r\n");
      out.write("\t\t\t// 시작일 datepicker 옵션 설정\r\n");
      out.write("\t\t\t// onSelect : 날짜 선택 시 종료일의 값 변경\r\n");
      out.write("\t        $(\"#onstudy-start\").datepicker({\r\n");
      out.write("\t          dateFormat: 'yy-mm-dd'\r\n");
      out.write("\t          ,minDate: \"1D\"\r\n");
      out.write("\t          ,maxDate: \"10D\"\r\n");
      out.write("\t          ,showOtherMonths:true\r\n");
      out.write("\t          ,showMonthAfterYear: true\r\n");
      out.write("\t          ,monthNames: ['1월','2월','3월','4월','5월','6월','7월','8월','9월','10월','11월','12월']\r\n");
      out.write("\t          ,monthNamesShort: ['1월','2월','3월','4월','5월','6월','7월','8월','9월','10월','11월','12월']\r\n");
      out.write("\t          ,dayNames: ['일','월','화','수','목','금','토']\r\n");
      out.write("\t          ,dayNamesShort: ['일','월','화','수','목','금','토']\r\n");
      out.write("\t          ,dayNamesMin: ['일','월','화','수','목','금','토']\r\n");
      out.write("\t          ,onSelect : function(){\r\n");
      out.write("\r\n");
      out.write("\t            var now = $(\"#onstudy-start\").datepicker(\"getDate\");\r\n");
      out.write("\t            year = now.getFullYear();\r\n");
      out.write("\t            month = now.getMonth();\r\n");
      out.write("\t            day = now.getDate();\r\n");
      out.write("\t            var now1 = new Date(year, month, day+(weeks*7)-1);\r\n");
      out.write("\r\n");
      out.write("\t            $(\"#onstudy-end\").datepicker(\"setDate\",now1);\r\n");
      out.write("\t          }\r\n");
      out.write("\t        });\r\n");
      out.write("\t\t\t// 시작일 기본값은 내일\r\n");
      out.write("\t        $(\"#onstudy-start\").datepicker(\"setDate\", \"+1D\");\r\n");
      out.write("\t       \r\n");
      out.write("\t\t\t// 기간의 값이 변하면 종료일 값 변경\r\n");
      out.write("\t        $(\"#onstudy-weeks\").on(\"change\", function(){\r\n");
      out.write("\r\n");
      out.write("\t          weeks = $(\"#onstudy-weeks\").val();\r\n");
      out.write("\r\n");
      out.write("\t          var now2 = $(\"#onstudy-start\").datepicker(\"getDate\");\r\n");
      out.write("\t          year = now2.getFullYear();\r\n");
      out.write("\t          month = now2.getMonth();\r\n");
      out.write("\t          day = now2.getDate();\r\n");
      out.write("\t          var now3 = new Date(year, month, day+(weeks*7)-1);\r\n");
      out.write("\t          $(\"#onstudy-end\").datepicker(\"setDate\",now3);\r\n");
      out.write("\r\n");
      out.write("\t        });\r\n");
      out.write("\t        \r\n");
      out.write("\t\t});\r\n");
      out.write("\t\t\r\n");
      out.write("\t\t\r\n");
      out.write("\t\t// 각각의 영역에 파일을 첨부 했을 경우 미리 보기가 가능하도록 하는 함수\r\n");
      out.write("\t    function LoadImg(value) {\r\n");
      out.write("\t    \t\r\n");
      out.write("\t    \t// 파일 업로드 시 업로드된 파일의 경로는 해당 요소에 files라는 배열이 생성되면 저장됨\r\n");
      out.write("\t    \tif(value.files && value.files[0]){\r\n");
      out.write("\t    \t\t// -> 파일이 선택된 경우\r\n");
      out.write("\t    \t\t\r\n");
      out.write("\t    \t\tvar reader = new FileReader(); // 해당 경로의 파일을 읽어들이는 것\r\n");
      out.write("\t    \t\t\r\n");
      out.write("\t    \t\t// 파일을 다 읽으면 실행해라\r\n");
      out.write("\t    \t\treader.onload = function(e){\r\n");
      out.write("\t    \t\t\t$(\"#thumbnailImg\").prop(\"src\", e.target.result);\r\n");
      out.write("\t    \t\t} \r\n");
      out.write("\t    \t\treader.readAsDataURL(value.files[0]);\r\n");
      out.write("\t    \t}\r\n");
      out.write("\t    }\r\n");
      out.write("\t\t\r\n");
      out.write("\t\tfunction sendEnd(){\r\n");
